# Setting up basic pandas variables
pd.options.display.max_columns = 15
pd.set_option('display.width', 500)

# Convert a value to an integer if possible
def tryToInt(val):
    try:
        val = int(val)
    except ValueError:
        return val
    return val

# Get a dataframe with certain values for certain columns. 
def dataFrameFind(colType0, colVal0, colType1, colVal1, rawDF):
    # Only return rows where the value in the colType column matches the corresponding colVal.
    return rawDF.loc[(rawDF[colType0] == tryToInt(colVal0)) & (rawDF[colType1] == tryToInt(colVal1))]

# Get the polygons of the world map
def getPolygons(geodf):
    arr = []
    for index, row in geodf.iterrows():
        arr.append(str(row['geometry']))
    return arr

# Parse x and y from the polygons. 
def parseXY(pol):
    x = []
    y = []
    # Split each polygon on the comma
    for i in pol.split(','):
        # Stores each pair of values in i
        i = re.sub(r'[^0-9 .-]', '', i)
        # Split the pair on the space inbetween them
        i = i.split(' ')
        # Append x and y values
        x.append(float(i[1]))
        y.append(float(i[2]))
    return x, y

# Load the geopandas worldmap into a dataframe
world = geopandas.read_file(geopandas.datasets.get_path('naturalearth_lowres'))
# Remove Antarctica and Fr. S. Antarctic Lands. There is no data for these places
# And they take up a lot of space. 
world = world[(world.name != "Antarctica") & (world.name != "Fr. S. Antarctic Lands")]

# Change the map projection. This line is extremely picky and does not like to 
# work in an IDE. It will generally do fine when run through the anaconda prompt.
# Use "bokeh serve --show TellAStoryGroupProj.py"
world = world.to_crs({'init' :'esri:54030'})

# Drop unnecessary columns
world = world.drop('continent', 1)
world = world.drop('iso_a3', 1)

# Getting polygons
polygonStrings = getPolygons(world)

# Parse and store the x and y values in an array
xTotal = []
yTotal = []
for i in polygonStrings:
    x, y = parseXY(i)
    xTotal.append(x)
    yTotal.append(y)

# Add the arrays as columns to the dataframe
world['x'] = xTotal
world['y'] = yTotal

