world = world[(world.name != "Malaysia") & (world.name != "Norway") & (world.name != 'New Zealand') & (world.name != 'Oman')]
world = world[(world.name != "Philippines") & (world.name != "Papua New Guinea") & (world.name != 'Russia') & (world.name != 'Solomon Is.')]
world = world[(world.name != "Turkey") & (world.name != "United States") & (world.name != 'Vanatu')]

# Add the single polygon versions of the countries back to the dataframe.
world = world.append(multiPolygons)

# Read the animal data.
rawDF = pd.read_csv("FAOData.csv")

# Drop unnecessary columns
rawDF = rawDF.drop("Domain Code", 1)
rawDF = rawDF.drop("Domain", 1)
