# Load human population data
humanDF = pd.read_csv('population.csv')
# Add the human data to the animal dataframe.
rawDF = rawDF.append(humanDF)

# Different names for the same country. No way to do this other than the
# gross 20 lines here. It sucks. It was annoying.
rawDF = rawDF.replace('Venezuela (Bolivarian Republic of)', 'Venezuela')
rawDF = rawDF.replace('Iran (Islamic Republic of)', 'Iran')
rawDF = rawDF.replace('United States of America', 'United States')
rawDF = rawDF.replace('Bosnia and Herzegovina', 'Bosnia and Herz.')
rawDF = rawDF.replace('Bolivia (Plurinational State of)', 'Bolivia')
rawDF = rawDF.replace('Brunei Darussalam', 'Brunei')
rawDF = rawDF.replace('Central African Republic', 'Central African Rep.')
rawDF = rawDF.replace('Democratic Republic of the Congo', 'Dem. Rep. Congo')
rawDF = rawDF.replace('Viet Nam', 'Vietnam') # This one is actually funny. Who thinks Vietnam is two words?
rawDF = rawDF.replace('Czechoslovakia', 'Czech Rep.')
